import React from 'react';
import { useParams } from 'react-router-dom';

const DettaglioCapitolo = () => {
  let { id } = useParams(); // `id` corrisponde all'identificativo dell'immagine cliccata

  // Qui puoi decidere come visualizzare i dettagli basandoti sull'id
  // Ad esempio, potresti avere un array o un oggetto che mappa gli id alle informazioni dei capitoli

  return (
    <div>
      <h2>Dettaglio del Capitolo {id}</h2>
      {/* Mostra qui i dettagli del capitolo basati sull'id */}
    </div>
  );
};

export default DettaglioCapitolo;
